import { wait } from "@testing-library/user-event/dist/utils";
import axios from "axios";

function UpdateTaskPage(): JSX.Element {
    const params=useParams();
    console.log(params.id)
    const {register,control,handleSubmit}=useForm<Tasks>();
    // const oldTask =
     axios.get(
        `http://localhost:4000/api/v1/taskboard/getTaskById/${params.id}`
    ).then((response)=>console.log(response.data.data))

    const onsubmit=()=>{

    }
    return (
        <div className="updateTaskPage">
			{`hello here we gonna update task id ${params.id}`} 
            <form onSubmit={handleSubmit(onsubmit)}>
            <textarea placeholder="enter the task right here!!!"
                {...register("content")}
                ></textarea>
                <br/>
                <select
                style={{height:"40px",width:"260px",marginTop:"10px",marginBottom:"10px"}} 
                {...register("type")}>
                    <option disabled selected>chose task importance</option>
                    <option>urgent</option>
                    <option>important</option>
                    <option>negligible</option>
                </select>
                <br/>
                <input type="reset" style={{background:"lightyellow"}}/>
                <input type="submit" 
                style={{background:"lightgreen"}}/>
            </form>
        </div>
    );
}

export default UpdateTaskPage;
