import "./singleTask.css";
import background from '../../../../img/notebg.png';
import { Button, IconButton } from "@mui/material";
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import { store } from "../../../redux/store";
import axios from "axios";
import {removeTask} from "../../../redux/tasksReducer"

interface itemProps{
    id:number;
    content:string;
    type:string;
}
function SingleTask(props:itemProps): JSX.Element {
    const deleteTask=(id:Number)=>{
        console.log(id);
      axios
      .delete(`http://localhost:4000/api/v1/taskboard/deleteTask/${id}`)
      .then(
        store.dispatch(removeTask(id))
      )

    }
    return (
        <div className="singleTask box" style={{backgroundImage:`url(${background})`}}>

            <IconButton aria-label="delete" 
            onClick={()=>deleteTask(props.id)}
            style={{ position: 'absolute', top: 0, right: 0,paddingTop:20}}>
  <DeleteOutlineIcon
/>
</IconButton>
<div className="taskInfo">
			{props.content}
            <br/>
            {props.type}
            <br/>
            </div>
        </div>
    );
}

export default SingleTask;
