function Tasks(): JSX.Element {
   let all_Tasks=[];
useEffect(()=>{
    if (store.getState().task.tasks.length<1){
        store.dispatch(
            downloadTask(JSON.parse(localStorage.getItem("tasks")as any))
        )
    }
},[])
store.subscribe(()=>{
    all_Tasks=store.getState().task.tasks
    console.log(all_Tasks);

})
    return (
        <div className="Tasks">
			{all_Tasks.length}

        </div>
    );
}

export default Tasks;
